				for(let i=0;i<getTariffs.data.length;i++) {
					if(getTariffs.data[i].priceunit.calculation == TariffFilter.selectedOptionValue) {
						res.push(getTariffs.data[i]);
					}
				}
		}
		for(let i=0;i<res.length;i++) {
			if(
				   ((res[i].minduration >= TimeFilter.selectedOptionValue - 60 ) && (res[i].minduration <= TimeFilter.selectedOptionValue + 60 ) ) ||
					 (((TariffFilter.selectedOptionValue == 'all') || (TariffFilter.selectedOptionValue == 'month') || (TariffFilter.selectedOptionValue == 'metered')) && ((res[i].priceunit.calculation == 'month') || (res[i].priceunit.calculation == 'metered')))
			  ) {
				res2.push(res[i]);
			}
		}
		return res2;
	},
	electricityForFull: () => {
		return capacity.text - ((soc.text/100) * capacity.text);
	}
}

